// EmployeeModal.js
import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EmployeeModal = ({ show, handleClose, dataId, onDelete }) => {
  const [employeeData, setEmployeeData] = useState({
    employeeName: '',
    address: '',
    age: '',
    department: '',
    employeeStatus: ''
  });

  useEffect(() => {
    if (dataId) {
      const fetchData = async () => {
        try {
          const data = await fetchEmployeeData(dataId);
          setEmployeeData(data);
        } catch (error) {
          console.error("Failed to fetch employee data:", error);
        }
      };
      fetchData();
    } else {
      // Reset data if dataId is not provided
      setEmployeeData({
        employeeName: '',
        address: '',
        age: '',
        department: '',
        employeeStatus: ''
      });
    }
  }, [dataId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({ ...employeeData, [name]: value });
  };

  const handleSubmit = async () => {
    try {
      await updateEmployeeData(dataId, employeeData);
      handleClose();
    } catch (error) {
      console.error("Failed to update employee data:", error);
    }
  };

  const handleDelete = async () => {
    try {
      await deleteEmployeeData(dataId);
      onDelete(dataId); // Call the onDelete prop to update the parent component
      handleClose();
    } catch (error) {
      console.error("Failed to delete employee data:", error);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Employee</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="employeeName">
            <Form.Label>Employee Name</Form.Label>
            <Form.Control
              type="text"
              name="employeeName"
              value={employeeData.employeeName || ''}
              onChange={handleChange}
              placeholder="Enter employee name"
            />
          </Form.Group>
          <Form.Group controlId="address">
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              name="address"
              value={employeeData.address || ''}
              onChange={handleChange}
              placeholder="Enter address"
            />
          </Form.Group>
          <Form.Group controlId="age">
            <Form.Label>Age</Form.Label>
            <Form.Control
              type="number"
              name="age"
              value={employeeData.age || ''}
              onChange={handleChange}
              placeholder="Enter age"
            />
          </Form.Group>
          <Form.Group controlId="department">
            <Form.Label>Department</Form.Label>
            <Form.Control
              type="text"
              name="department"
              value={employeeData.department || ''}
              onChange={handleChange}
              placeholder="Enter department"
            />
          </Form.Group>
          <Form.Group controlId="employeeStatus">
            <Form.Label>Employee Status</Form.Label>
            <Form.Control
              type="text"
              name="employeeStatus"
              value={employeeData.employeeStatus || ''}
              onChange={handleChange}
              placeholder="Enter employee status"
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="danger" onClick={handleDelete}>
          Delete
        </Button>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EmployeeModal;

const fetchEmployeeData = async (id) => {
  const response = await fetch(`/api/employees/${id}`);
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
  return await response.json();
};

const updateEmployeeData = async (id, data) => {
  const response = await fetch(`/api/employees/${id}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  });
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
};

const deleteEmployeeData = async (id) => {
  const response = await fetch(`/api/employees/${id}`, {
    method: 'DELETE',
  });
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
};
