import React, { useEffect, useState } from 'react';
import axios from 'axios';
import UserIcon from "../../../images/user-icon.png";
import MapBox from './MapBox';
import { MAIN_URI } from '../../helper';

const EmployeeModal = ({ refHandle, dataId, onUpdate, onDelete }) => {
  const [empData, setEmpData] = useState({});
  const [editMode, setEditMode] = useState(false);
  const [updatedEmployee, setUpdatedEmployee] = useState({});

  useEffect(() => {
    const loadData = async () => {
      const { data } = await axios.get(`${MAIN_URI}/api/v1/read/${dataId}`);
      setEmpData(data.employee);
      setUpdatedEmployee(data.employee);
    };

    if (dataId) {
      loadData();
    }
  }, [dataId]);

  const handleChange = (e) => {
    setUpdatedEmployee({ ...updatedEmployee, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    await axios.put(`${MAIN_URI}/api/v1/update/${dataId}`, updatedEmployee);
    onUpdate();
    setEditMode(false);
  };

  const handleDelete = async () => {
    await axios.delete(`${MAIN_URI}/api/v1/delete/${dataId}`);
    onDelete();
  };

  const closeModal = (e) => {
    e.preventDefault();
    e.target.classList.remove('show-modal');
  };

  return (
    <>
      <div className='main-wrap' ref={refHandle} onClick={(e) => closeModal(e)}>
        <div className='inner-box p-3'>
          {!empData ? (
            <div>...loading</div>
          ) : (
            <>
              <div className="row">
                <div className="col-md-5">
                  <div className="user-card">
                    <div className="user-card-img">
                      <img src={UserIcon} alt="user icon" />
                    </div>
                    <div className="about-user text-center">
                      <span>{editMode ? <input type="text" name="employeeName" value={updatedEmployee.employeeName} onChange={handleChange} /> : empData.employeeName}</span>
                      <span>Age: {editMode ? <input type="number" name="age" value={updatedEmployee.age} onChange={handleChange} /> : empData.age}</span>
                      <span>Department: {editMode ? <input type="text" name="department" value={updatedEmployee.department} onChange={handleChange} /> : empData.department}</span>
                      <span>Status: {editMode ? <input type="text" name="employeeStatus" value={updatedEmployee.employeeStatus} onChange={handleChange} /> : empData.employeeStatus}</span>
                    </div>
                  </div>
                </div>
                <div className="col-md-7">
                  <div className='bg-info text-white p-2 rounded'>
                    <h5 className='my-1'>Address</h5>
                    <span className='text-white'>{editMode ? <input type="text" name="address" value={updatedEmployee.address} onChange={handleChange} /> : empData.address}</span>
                  </div>
                  <MapBox locationData={empData.address} />
                </div>
              </div>
              <div className='row mt-3'>
                <div className='col-md-12 text-center'>
                  {editMode ? (
                    <>
                      <button className="btn btn-success mx-2" onClick={handleUpdate}>Update</button>
                      <button className="btn btn-secondary mx-2" onClick={() => setEditMode(false)}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button className="btn btn-primary mx-2" onClick={() => setEditMode(true)}>Edit</button>
                      <button className="btn btn-danger mx-2" onClick={handleDelete}>Delete</button>
                    </>
                  )}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default EmployeeModal;
